mapper.update:
update customer
set
     email = #{email},
     tel = #{tel},
     addr = #{addr}
     district = #{district},
     city = #{city},
     province = #{province}
where customerid = #{customerId}

mapper.select:
select customerid, cname as name, email, tel, addr, district, city, province
from customer
where
   1 = 1

    and cname like '%${name}%'


    and district = #{district}


    and city = #{city}


    and province = #{province}
