mapper.select:
select itemid, cid as categoryId, name, unitcost, listprice, descn, img as imgUrl, status
from item
where cid = #{categoryId}

select itemid, cid as categoryId, name, unitcost, listprice, descn, img as imgUrl, status
from item
where itemid = #{itemId}

mapper.update:
update item
set status = #{status}
where itemid = #{itemId}

update item
set
   listprice = #{listPrice},
   descn = #{descn},
   img = #{imgUrl}
where itemid = #{itemId}
