mapper.update:
update orderstatus
set
    processstatus = #{processStatus}
where orderid = #{orderId}

update orderstatus
set
    payStatus = #{payStatus}
where orderid = #{orderId}

update orders
set
    shippingcost = #{shippingCost}
where orderid = #{orderId}

mapper.select:
select count(*)
from orders
where orderid = #{orderId}
