mapper.update:
update orderstatus
set
    processstatus = #{processStatus}
where orderid = #{orderId}

update orderstatus
set
    payStatus = #{payStatus}
where orderid = #{orderId}

update orders
set
    shippingcost = #{shippingCost}
where orderid = #{orderId}

mapper.select:
select count(*)
from orders
where orderid = #{orderId}

select orders.orderid, createdate as "createDateTime", billname, totalprice, processstatus from orders,orderstatus where orders.orderid = orderstatus.orderid and date(createdate) between #{dateFrom} and #{dateTo}
and billname like '%${billName}%'
and processstatus = UPPER(#{processStatus})
and orders.orderId = #{orderId}
order by createdate
