mapper.select:
select recordid, supplierid, mainid as mainSiteId, itemid, qty, timestamp
from supplyio
where recordid = #{recordId}

select recordid, supplierid, mainid as mainSiteId, itemid, qty, timestamp
from supplyio
where supplierid = #{supplierId}

select recordid, supplierid, mainid as mainSiteId, itemid, qty, timestamp
from supplyio
where mainid = #{mainSiteId}

select recordid, supplierid, mainid as mainSiteId, itemid, qty, timestamp
from supplyio
where itemid = #{itemId}
