mapper.select:
select warehouseid, mainsiteid as siteId, categoryid, maxsize,
from warehouse
where mainsiteid = #{mainSiteId}

select count(itemid) as kindOfItem, sum(num) as totalSize
from inventory
where warehouseid = #{warehouseId}

select itemid
from inventory
where warehouseid = #{warehouseId}

select itemid, num
from inventory
where warehouseid = #{warehouseId}
